	$core_svgs = array();
	
	function svg($id, $height, $width, $color) {
		global $core_svgs;
		$svgIcon = $core_svgs[$id];
		
		echo '<svg role="img" viewBox="0 0 1792 1792" ';
		if ($height) echo 'height="' . $height . '" ';
		if ($width) echo 'width="' . $width . '" ';
		if ($color) echo 'fill="' . $color . '" ';
		echo '><use xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#' . $id . '"></use></svg>';
	}
	
	function svgDegs() {
		global $core_svgs;
		global $lazy_svgs;
		global $desktop_svgs;

		echo '<svg style="display:none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" viewBox="0 0 512 512">';
		echo '<defs id="svg-defs">';
		echo Svgs2Defs($core_svgs);
		echo Svgs2Defs($lazy_svgs);
		//if (!isMobile()) echo Svgs2Defs($desktop_svgs);
		echo Svgs2Defs($desktop_svgs);
		echo '</defs>';
		echo '</svg>';
	}
	
	function Svgs2Defs($svgs) {
		$defs = "";
		
		foreach($svgs as $key => $svg) {
			$svg = str_replace('<svg width="1792" height="1792" viewBox="0 0 1792 1792" xmlns="http://www.w3.org/2000/svg"><path', '<path', $svg);
			$svg = str_replace('/></svg>', '></path>', $svg);
		
			$defs .= '<g id="' . $key . '">';
			$defs .= $svg;
			$defs .= '</g>';
		}
		
		return $defs;
	}
	
