app['timer'] = new function() {
	_timer = this;
	this.timers = {}

	this.init = function() {
		EventManager.observe('loadFirstScreen', _timer.loadTime);
	}
	
	this.loadTime = function() {
		var loadTimeId = "load-time",
			loadStart = window.loadStart;
		
		if (loadStart) {
			_timer.timers[loadTimeId] = {};
			_timer.timers[loadTimeId]['start'] = loadStart;
			_timer.endTimer(loadTimeId);
		}
	}

	this.startTimer = function(id) {
		_timer.timers[id] = {};
		_timer.timers[id]['start'] = new Date().getTime();
	}

	this.endTimer = function(id) {
		if (_timer.timers[id]) {
			_timer.timers[id]['end'] = new Date().getTime();
			_timer.timers[id]['time'] = _timer.timers[id]['end'] - _timer.timers[id]['start'];
			
			// Find divisible
			var mod = 1;
			$.each([1000, 100, 10], function(x, i) { 
				if (_timer.timers[id]['time'] >= i) {
					mod = i;
					return false;
				}
			});
	
			// Track timer
			timeTrack = Math.round(_timer.timers[id]['time'] / mod) * mod;
			app.gtrack.track_event('timer', id, timeTrack);
		}
	}
}();
app['global'] = new function() {
	var _global = this;
	this.els = {};
	
	this.init = function() {
		_global.getEls();
	}
	
	this.getEls = function() {
		_global.els = {
			spinner	: $('div#spinner'),
			dialog	: $('div#dialog'),
			alert	: $('div#alert'),
			body	: $('body'),
			content	: $('content')
		}
	}
	
	this.removeHTML = function(string) {
		return (string.replace(/(<([^>]+)>)/ig,""));
	}

	this.objValue = function(obj, scope) {
		try {
			if (obj.hasOwnProperty(scope)) return obj[scope];
			else return null;
		} catch (err) {
			return null;
		}
	}

	this.toggleScroll = function(scroll) {
		if (typeof scroll != 'boolean') scroll = true;
		
		$('body').css({
			'overflow': (scroll ? 'visible' : 'hidden')
		})
	}
	
	this.fileExtension = function(file) {
		return (/[.]/.exec(file)) ? /[^.]+$/.exec(file) : undefined;
	}
	
	this.dialog = function(params) {
		
		// Build params
		if (!params) params = new Object();
		if (!params['msg']) params['msg'] = ' ';
		if (!params['title']) params['title'] = 'Alert!';
		if (!params['resizable']) params['resizable'] = false;
		if (!params['modal']) params['modal'] = true;
		if (!params['position']) params['position'] = 'center';
		if (!params['draggable']) params['draggable'] = false;
		if (params['hideTitle']) params['dialogClass'] = 'noTitleStuff';
		if (!params['buttons']) params['buttons'] = new Object();
		if (!params['width']) params['width'] = 320;
		if (!params['height']) params['height'] = 'auto';
		if (!params['animate']) params['animate'] = true;
		if (!params['transition']) params['transition'] = 'slideInDown';
		if (!params['transitionSpeed']) params['transitionSpeed'] = 'default';

		// Show Dialog
		_global.dialogClose();
		_global.els['dialog']
			.append(params['msg'])
			.dialog(params)
		_global.els['dialogForm'] = _global.els['dialog'].parent();
		$(_global.els['dialogForm']).center();
			
		if (params['animate']) {
			app.global.animate(_global.els['dialogForm'], params['transition'], params['transitionSpeed'], function() {
				if (params.openCB) params.openCB();
			});
		} else {
			if (params.openCB) params.openCB();
		}
		return _global.els['dialogForm'];
	}
	
	this.dialogClose = function() {
		if (_global.els['dialog'].hasClass('ui-dialog-content')) {
			_global.els['dialog']
				.empty()
				.dialog('close');
		}
	}
	
	this.addToHomescreen = function() {
		var storageName = 'addToHomescreen';

		addToHomescreen({
			debug: (app.config.env == 'dev' ? true : false),
			startDelay: 0,
			onAdd: function() {
				app.ls.setItem(storageName, true)
			}
		});
	}

	this.dialogConfirm = function(params) {
		if (!params) params = new Object();
		if (!params['msg']) params['msg'] = 'Would you like to save?';
		if (!params['yesBtn']) params['yesBtn'] = 'Yes';
		if (!params['noBtn']) params['noBtn'] = 'No';
		if (!params['error']) params['error'] = false;
		if (!params['title']) params['hideTitle'] = true;

		dialogParams = {
			msg: params['msg'],
			buttons: {
				"save": {
					text: params['yesBtn'],
					class: 'width_50 right',
					click: function() {
						if (params['saveCallback']) params['saveCallback']();
						if (!_global.els['dialog'].dialog('option', 'error')) {
							_global.dialogClose();
							if (params['saveCloseCallback']) params['saveCloseCallback']();
						}
					}
				},
				"cancel": {
					text: params['noBtn'],
					class: 'width_50 left',
					click: function() {
						if (params['cancelCallback']) params['cancelCallback']();
						_global.dialogClose();
						if (params['cancelCloseCallback']) params['cancelCloseCallback']();
					}
				}
			}
		};
		$.extend(params, dialogParams);
		
		app.global.dialog(params);
	}
	
	this.dialogToggleError = function(params) {
		if (!params) params = new Object();
		if (!params['error']) params['error'] = false;
		
		if (_global.els['dialog']) {
			_global.els['dialog'].dialog('option', 'error', params['error']);
		}
	}
	
	this.animate = function(el, animation, speed, callback) {
		if (!$(el)) return false;
		if (!animation) animation = 'bounce';
		if (!speed || speed == 'default') speed = 'animated_0_5';
		if (speed == 'fast') speed = 'animated_0_2';
		if (speed == 'slow') speed = 'animated';


		console.log('Animate:', animation, speed);
		$(el)
			.one('webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend', function() {
				$(el)
					.removeClass(speed)
					.removeClass(animation)
				if (callback) callback();
			})
			.addClass(animation)
			.addClass(speed);
	}
	
	this.inputToggleError = function(el, error) {
		if (!$(el).hasClass('input-group')) el = $(el).parents('.input-group').first();
		$(el)[error ? 'addClass' : 'removeClass']('has-error');
	}
	
	this.alert = function(params) {
		if (!params) params = new Object();

		var alert = _global.els.alert,
			icon = (params['icon'] ? $.tmpl(app.templates.icon, { 
				classes: 'fa-lg fa-white', 
				icon: params['icon'] 
			}) : ''),
			msg = (params['msg'] ? $('<span>' + params['msg'] + '</span>') : ''),
			animationIn = 'bounceIn',
			animationOut = 'bounceOut',
			speed = 'default';

		alert
			.empty()
			.append(icon)
			.append(msg)
			.center()
			.show()
		
		app.global.animate(alert, animationIn, speed, function() {
			setTimeout(function() {
				app.global.animate(alert, animationOut, speed, function() {
					alert.hide();
					if (params['cb']) params['cb']();
				});
			}, 250);
		})
			
	}
}();
app['ajax'] = new function() {
	_ajax = this;
	this.queue = [];
	this.queueStorageId = 'ajaxQueue';
	
	this.init = function() {
		
		// Add global ajax events
		$.ajaxSetup({
			beforeSend: _ajax.ajaxBeforeSend,
			complete: _ajax.ajaxComplete,
			error: _ajax.ajaxError
		});
		
		// Get stored items in queue
		if (app.ls.getItem(_ajax.queueStorageId)) _ajax.queue = app.ls.getItem(_ajax.queueStorageId);
	}
	
	this.cleanUrl = function(url) {
		if (url.indexOf('?') != -1) {
			url = url.slice(0, url.indexOf('?'));
		}
		return url;
	}
	
	/*
	 * Called before request sent
	 */
	this.ajaxBeforeSend = function(x, s) {
		$(document).overlay({ show: true, id: 'ajax' });
		if (s && s.url) {
			var url = _ajax.cleanUrl(s.url);
			app.timer.startTimer(url);
		}
	} 

	/*
	 * Called once received any response
	 */
	this.ajaxComplete = function(e, x, s) {
		var url = _ajax.cleanUrl(this.url);
		
		_ajax.ajaxResponseLog(e);
		_ajax.trackRequest(x, url);
		$(document).overlay({ show: false, id: 'ajax' });
		app.timer.endTimer(url);
	}

	/*
	 * Called if error response
	 */
	this.ajaxError = function() {
		$(document).overlay({
			show: true,
			delay: 2000,
			msg: "Sorry, there was an error!"
		});
	}
	
	/*
	 * If user is offline store ajax request
	 */
	this.addToQueue = function(s) {
		_ajax.queue.push(s);
		app.ls.setItem(_ajax.queueStorageId, _ajax.queue);
	}

	/*
	 * Send ajax details to GA
	 */
	this.trackRequest = function(gtAct, gtLabel) {
		app.gtrack.track_event('app:ajax', gtAct, gtLabel);
	}

	/*
	 * Console log the ajax response
	 */
	this.ajaxResponseLog = function(e) {
		try {
			if (e && e.responseText) {
				var response = $.parseJSON(e.responseText)
				console.log('Response:', response); 
			}
		} catch (e) { console.log('Response: ERROR'); }
	}
}
app['header'] = new function() {
	var _header = this;
	this.els = {}

	this.data = {
		scrollDelta: 5,
		scrolled: false,
		hasShadow: false,
		currScroll: 0,
		lastScroll: 0
	};

	this.templates = {
		'backText'		: '<span class="text-shadow"></span>',
		'backWrapper'	: '<div class="back-btn-wrapper left pointer"></div>',
		'searchWrapper'	: '<div class="search-wrapper"></div>',
		'searchField'	: '<div class="input-group input-group"></div>',
		'iconWrapper'	: '<span class="input-group-addon"></span>',
		'inputSearch'	: '<input id="test" type="text" class="form-control" placeholder="Search for user">',
		'userField'		: '<div class="userField"></div>',
		'userImg'		: '<img class="userImg" src="${image}sz=30">',
		'userName'		: '<div class="userName text-shadow">${name}</div>'
	}

	this.template_data = {
		'logoWrapper': {
			'classes'		: 'left logo-wrapper text-shadow'
		},
		'logoIcon': {
			'icon'		: 'comments-o',
			'classes'	: 'header__logo-icon',
			'gtAct'		: 'logo'
		},
		'backBtnIcon': {
			'icon'			: 'chevron-left',
			'classes'		: 'fa-lg header__back-icon',
			'gtAct'			: 'back'
		},
		'iconSearch': {
			'icon'			: 'search',
			'gtAct'			: 'search:go'
		},
		'iconCancel': {
			'icon'			: 'times-circle',
			'gtAct'			: 'search:cancel'
		},
		'iconUser': {
			'classes'		: 'pointer header__user',
			'icon'			: 'user',
			'gtAct'			: 'signout'
		},
		'helpWrapper': {
			'classes'		: 'right text-shadow help-wrapper'
		},
		'help': {
			'classes'		: 'pointer header__help',
			'icon'			: 'question-circle',
			'gtAct'			: 'help'
		},
		'signup': {
			'classes'		: 'header__sign-up-btn',
			'input'			: 'Sign Up'
		}
	}
	
	this.init = function() {
		_header.getEls();
		_header.scroll();
	}
	
	this.getEls = function() {
		_header.els = {
			parent: $('#header-fields'),
			header: $('header')
		}
	}
	
	this.scroll = function() {
		// Scroll listener
		$(window).scroll(function() {
			_header.data.scrolled = true;
		})
		
		// Check if scrolled
		setInterval(function() {
			if (_header.data.scrolled) {
				_header.data.scrolled = false;
				_header.data.currScroll = $(window).scrollTop();
				
				// Ignore minimal scroll
				if (! (Math.abs(_header.data.lastScroll - _header.data.currScroll) <= _header.data.scrollDelta) ) {
					_header.scrollHideHeader();
					_header.toggleShadow();
				}
			}
		}, 250);
	}
	
	/*
	 * Add shadow to header if on top of elements
	 */
	this.toggleShadow = function() {
		if (_header.data.currScroll > 10 && !_header.data.hasShadow) {
			_header.els.header.addClass('shadow');
			_header.data.hasShadow = true;
		} 
		else if (_header.data.currScroll <= 10 && _header.data.hasShadow) {
			_header.els.header.removeClass('shadow');
			_header.data.hasShadow = false;
		}
	}

	/*
	 * Add shadow to header if on top of elements
	 */
	this.scrollHideHeader = function() {
		var headerHeight = _header.els.header.outerHeight();

		if (_header.data.currScroll > _header.data.lastScroll && _header.data.currScroll > headerHeight) {
			// Hide header
			_header.els.header
				.removeClass('nav-down')
				.addClass('nav-up');
		} else {
			if (_header.data.currScroll + $(window).height() < $(document).height()) { 
				// Show header
				_header.els.header
					.removeClass('nav-up')
					.addClass('nav-down');
			}
		}
		
		// Save last scroll
		_header.data.lastScroll = _header.data.currScroll;

	}

	this.destroy = function() {
		// Delete Search Field
		if (_header.els.hasOwnProperty('searchField')) _header.destroySearch();

		_header['els']['parent'].empty();
		//_header.els = new Object();
	}
	
	this.show = function() {
		var animation = 'fadeInDown',
			speed = 'default';
		
		app.global.animate(_header['els']['parent'].children(), animation, speed, function() {
			EventManager.fire('header:show');
		});
	}
	
	this.toggle = function(show) {
		if (typeof show != 'boolean') show = true;
		$('header')[show ? 'show' : 'hide']();
	}
	
	this.addBackButton = function() {
		var backWrapper = $.tmpl(_header.templates.backWrapper, {}),
			backBtnTxt = $.tmpl(_header.templates.backText, {}),
			backBtnIcon = $.tmpl(app.templates.svg, _header.template_data['backBtnIcon']);
		
		$(backWrapper)
			.append(backBtnIcon)
			.append(backBtnTxt)
			.click(app.controller.prevSlide);
		
		_header['els']['parent']
			.empty()
			.append(backWrapper)
			.show();
	}
	
	this.addLogo = function() {
		var logoWrapper = $.tmpl(app.templates.div, _header.template_data['logoWrapper']),
			logoIcon = $.tmpl(app.templates.svg, _header.template_data['logoIcon']);
			
		logoWrapper.append(logoIcon);
		_header['els']['parent'].append(logoWrapper);
	}
	
	this.addSignUpButton = function() {
		var helpWrapper = $.tmpl(app.templates.div, _header.template_data['signup']);
		
		_header.els.parent.append(helpWrapper);
		helpWrapper.click(function() {
			window.location.href = "/signup";
		})
	}
	
	this.addHelp = function() {
		var helpWrapper = $.tmpl(app.templates.div, _header.template_data['helpWrapper']),
			helpIcon = $.tmpl(app.templates.svg, _header.template_data['help']),
			userName = $.tmpl(_header.templates.userName, { name: 'Help!' });
			
		helpWrapper
			.append(helpIcon)
			.append(userName);
		_header['els']['parent'].append(helpWrapper);
		
		helpWrapper.click(function() {
			app.email.init({
				'placeholder': 'I had the following issue ...',
				'subject': 'Error Email',
				'to': false,
				'api': 'backend/backend/_services.php?service=helpEmail'
			});
		})
	}

	/************** LOGIN ***************/
	this.addUserField = function() {
		var isLoggedIn = app.login.getLoggedInStatus(),
			userField = $.tmpl(_header.templates.userField),
			userName = $.tmpl(_header.templates.userName), 
			userImg = $.tmpl(app.templates.svg, _header.template_data['iconUser']);

		// Add UI
		$(userField)
			.append($(userImg))
			.append($(userName));
		_header.addField($(userField));
		_header.els['userField'] = $(userField);
		_header.els['userImg'] = $(userImg);
		_header.els['userName'] = $(userName);

		// Add Events
		EventManager.observe('logout', _header.toggleUser);
		EventManager.observe('login', _header.toggleUser);
		
		_header.els['userField'].click(function() {
			var isLoggedIn = app.login.getLoggedInStatus();
			app.login[isLoggedIn ? 'showLogout' : 'loginDialog']();
		})

		// Show
		if (isLoggedIn) {
			_header.userLoggedIn();
		} else {
			_header.userLoggedOut();
		}
		
	}
	
	this.toggleUser = function() {
		app.global.animate(_header.els['userField'], 'flipOutY', null, function() {
			var isLoggedIn = app.login.getLoggedInStatus();
			app.header[isLoggedIn ? 'userLoggedIn' : 'userLoggedOut']();
			app.global.animate(_header.els['userField'], 'flipInY');
		});
	}
	
	this.userLoggedOut = function() {
		_header.els['userName']
			.text('Login');
	}

	this.userLoggedIn = function() {
		_header.els['userName']
			.text(app['data']['user']['name']);
	}
	
	this.addSearch = function(searchField) {
		var speed = 'default';

		_header.els['searchField'] = searchField;
		_header.addField(searchField);
		app.global.els['body'].addClass('hasSearchField');
	}
	
	this.destroySearch = function() {
		// Remove Element
		_header.els['searchField'].remove();
		delete _header.els['searchField'];
		
		// Cleanup 
		app.global.els['body'].removeClass('hasSearchField');
		$(document).unbind('scroll.search');
	}
	
	this.addField = function(el) {
		_header['els']['parent']
			.append(el)
			.show();
	}
}();
app['gtrack'] = new function() {
	_gtrack = this;
	this.trackable_tags = ["a", "input", "button", "i", "textarea", "select"];
	this.category = null;
	
	this.init = function() {
		// Set page category
		_gtrack.category = _gtrack.pageCategory();
		
		// Add click handlers
		$.each(_gtrack.trackable_tags, function(x, selector) {
			$(document.body).on('click', selector, function(event) {
				var el = $(event.currentTarget).closest(selector)[0];
				_gtrack.click(el);
			});
		})
		
	}

	this.click = function(el) {
		var gt_act = $(el).attr('data-action'),
			gt_label = $(el).attr('data-label'),
			gt_cat = null;

		// Category
		if (_gtrack.elCategory(el)) gt_cat = _gtrack.elCategory(el);

		// Action
		if (!gt_act) gt_act = $(el).prop('tagName').toLowerCase();
		gt_act = 'click:' + gt_act;

		_gtrack.track_event(gt_cat, gt_act, gt_label);
	}
	
	this.pageCategory = function() {
		var gt_page = window.location.pathname.replace(/\//g, '');
		
		return (gt_page ? gt_page : 'app');
	}
	
	this.elCategory = function(element) {
		try {
			var count = 0, max = 99;
			while (element != document && count < max) {
				var parent = $(element).parents();
				if (parent.attr('data-category')) {
					return parent.attr('data-category'); 
				} else {
					element = parent; 
					count++; 
				}
			}
			return null;
		} catch(e) {return null;}
	}

	this.track_event = function(category, action, label) {
		if ( typeof category == 'undefined' || !category ) {
			category = _gtrack.category;
		} else {
			category = _gtrack.category + "/" + category;
		}
		if ( typeof action == 'undefined' || !action ) action = '';
		if ( typeof label == 'undefined' || !label ) label = '';

		if (typeof ga != 'undefined' && app.config.env != 'dev') {
			ga('send', 'event', category, action, label);
		} else {
			console.log('GA Event:', category, action, label);
		}
	}


}
app['controller'] = new function() {
	_controller = this;
	this.firstScreen = null;
	this.screenListOrder = [];
	this.subScreen = null;
	this.animate = {
		'speed': 'default',
		'in': 'slideInLeft',
		'out': 'slideOutRight'
	}
	
	this.init = function() {
		_controller.firstScreen = $('body').attr('data-firstScreen')
	}
	
	this.loadFirstScreen = function() {
		
		var currSlide = app[_controller.firstScreen]; //_controller.currSlide();
		console.log(_controller.firstScreen);
		//console.log('currSlide', currSlide);
		//if (currSlide) currSlide.els['parent'].empty();
		
		_controller.screenListOrder = [];
		_controller.screenListOrder.push(currSlide);

		//_controller.showSlide();
		currSlide['init']()
		EventManager.fire('loadFirstScreen');
	}
	
	/*
	 * Move forward to a new slide
	 */
	this.nextSlide = function(nextSlide) {
		var currSlide = _controller.currSlide();

		_controller.screenListOrder.push(nextSlide);
		_controller.slideTransfer(currSlide, nextSlide);
	}
	
	this.prevSlide = function() {
		var currSlide = _controller.currSlide();
		_controller.screenListOrder.pop();
		var nextSlide = _controller.currSlide();

		_controller.slideTransfer(currSlide, nextSlide);
	}
	
	this.currSlide = function() {
		return _controller.screenListOrder[_controller.screenListOrder.length - 1];
	}
	
	this.slideTransfer = function(currSlide, nextSlide) {

		// Setup transfer
		app.header.destroy(_controller.animate.out, _controller.animate.speed);

		$(document).overlay({ show: true });
		app.global.animate(currSlide.els['parent'], _controller.animate.out, _controller.animate.speed, function() {

			if (currSlide.els['parent']) currSlide.els['parent'].empty();
			currSlide.els = {};
			
			// Show next slide
			_controller.showSlide();
			$(document).overlay({ show: false });
		});
	}

	
	this.showSlide = function() {
		var speed = 'default',
			animation = 'slideInLeft',
			currSlide = _controller.currSlide();

		// Make sure slide is empty
		if (currSlide.els['parent']) currSlide.els['parent'].empty();
		
		// Scroll back to top
		window.scrollTo(0, 0);

		$.when(currSlide['init']()).done(function() {
			app.header.show(_controller.animate.in, _controller.animate.speed);
			app.global.animate(currSlide.els['parent'], _controller.animate.in, _controller.animate.speed, function() {
			});
		});
	}
};
app['libs'] = new function() {
	_libs = this;
	this.loaded = [];
	
	this.init = function() {
		EventManager.observe('lib:loaded', this.libLoaded);
	}
	
	this.libLoaded = function(params) {
		if (!params) params = {};
		if (!params.lib) params.lib = "";

		_libs.loaded.push(params.lib);
		EventManager.fire(params.lib + ":loaded");
	}
	
	this.isLoaded = function(lib) {
		return ($.inArray(lib, _libs.loaded) >= 0 ? true : false);
	}
	
	this.waitForLib = function(params) {
		if (!params) params = {};
		if (!params.lib) params.lib = "";
		if (!params.cbParams) params.cbParams = {};
		
		if (_libs.isLoaded(params.lib)) {
			if (params.cb) params.cb(params.cbParams);
		} else {
			$(document).overlay({ show: true });
			EventManager.observe_once(params.lib + ":loaded", function() {
				$(document).overlay({ show: false });
				if (params.cb) params.cb(params.cbParams);
			});
		}
	}
}();
